class Solution:
    def canCompleteCircuit(self, gas: List[int], cost: List[int]) -> int:
        start=end=rgas=0
        n=len(gas)
        while start<n:
            rgas+=(gas[end%n]-cost[end%n])
            if (end+1)%n==start and rgas>=0:
                return start
            while rgas<0 and start<=end and start<len(gas):
                rgas-=(gas[start]-cost[start])
                start+=1

            end+=1
        return -1
