class Solution {
    public int canCompleteCircuit(int[] gas, int[] cost) {

        int N = gas.length;
        int guess = 0;
        int cum = 0;
        int sum = 0;

        for(int i = 0; i < N; ++i){
            cum += (gas[i] - cost[i]);
            sum += (gas[i] - cost[i]);

            if(cum < 0){
                cum = 0;
                guess = (i + 1) % N;
            }
        }

        return (sum >= 0) ? guess : -1;
    }
}
