class Solution {
    public int canCompleteCircuit(int[] gas, int[] cost) 
    {
        int len = gas.length;
        for(int i=0; i<len; i++)
        {
            int gasRem = 0;
            for(int j=i;j<len; j++)
            {
                gasRem += gas[j]-cost[j];
                if(gasRem>=0)
                    continue;
                else
