              class Solution:
def canCompleteCircuit(self, gas: List[int], cost: List[int]) -> int:
    start, n, s = 0, len(gas), 0

    for i in range(2 * n):
        s += gas[i % n] - cost[i % n]

        if s < 0: s, start = 0, i + 1
        if i >= start + n: return start
    return -1

def canCompleteCircuit(self, gas: List[int], cost: List[int]) -> int:
    if sum(gas) < sum(cost): 
        return -1
    n, start, res = len(gas), 0, 0
    for i in range(n):
        res += gas[i] - cost[i]
        if res < 0:
            start, res = i+1, 0
    return start



