        //difference betweem hour hand position and minutes hand position multipled with 6
        //So angle 1 = (abs(hr - minutes))*6
        //The other angle will be (360 - angle 1)
        //return the smaller one

        double a = min((abs(hr - minutes))*6, abs(360 - (abs(hr - minutes))*6));

        return a;
    }
