        double hHandMove = minFrac * oneHourAngle;

        //calculating the angles of hour hand the minute hand from 0degree
        double hAngle = (hour%12) * oneHourAngle + hHandMove;
        double minAngle = minutes * oneMinAngle;

        //calculating both the angles formed 
        double firstDiff = Math.abs(hAngle - minAngle);
        double secondDiff = 360 - firstDiff;

        return Math.min(firstDiff, secondDiff);

    }
