{
	unordered_map<int,vector<int>> adj;
    int n=arr.size();
    for(int i=0;i<n;++i)
    	if(adj.find(arr[i])==adj.end())
    		adj[arr[i]]={i};
    	else adj[arr[i]].push_back(i);
    vector<int> grey(n,0);
    grey[0]=1;
    queue<pair<int,int>> q;
    q.push({0,0});
