class Solution {
public:
    int minJumps(vector<int>& arr) {
        unordered_map<int,vector<int>>m; //to store the index of the same value
        int n=arr.size();
        for(int i=0;i<n;i++){
            m[arr[i]].push_back(i);
        }
        queue<int>q;
        q.push(0);
        vector<int>vis(n);
        int level=0;
        while(q.empty()==false){
            int sz=q.size();
            while(sz--){
                auto front=q.front();
                q.pop();
                if(front==n-1){         //if current node is last node return current level
                    return level;
                }
                vis[front]=1;                   //mark current node
                auto &temp=m[arr[front]];           //get list of nodes which are having same value as current node
                for(auto &i:temp){
                    if(vis[i]==0){             //if any node is not visited 
                        if(i==n-1)            // check if it is last node return level+1 (because it will be find it in next round)
                            return level+1;
                        q.push(i);           // otherwise mark as visited and add in queue
                        vis[i]=1;
                    }
                }
                temp.clear();            //clear the array since we have visited all the nodes or will be visiting in future but added in queue
                if(front+1==n-1){       //if front+1 is last then we can directly jump to it and reach to destination so return level +1
												  //no need to check for front-1 is the last or not because if we are at index n then only we can reach to n-1 by jumping left but we can never go to n (mention in question)
                    return level+1;
                }
                if(front-1>=0 and vis[front-1]==0){        //check if we can jump to the left then we will add in the queue and mark as visited
                    q.push(front-1);
                    vis[front-1]=1;
                }
                if(front+1<n and vis[front+1]==0){         //check if we can jump to the right then we will add in the queue and mark as visited
                    q.push(front+1);
                    vis[front+1]=1;
                }
            }
            level++;
        }
        return -1;
    }
};
