class Solution {
public:
    int minJumps(vector<int>&arr) 
    {
        //BFS
        vector<bool>reachedindex(arr.size(),false);
        unordered_map<int,vector<int>>indexofval;
        for(int i=0;i<arr.size();i++)
        {
            indexofval[arr[i]].push_back(i);
        }
        queue<int>toreach;
        toreach.push(0);
        reachedindex[0]=true;
        int steps=0;
        while(!toreach.empty())
        {
            int n=toreach.size();
            while(n--)
            {
                int x=toreach.front();
                if(x==arr.size()-1)
                {
                    return steps;
                }
                toreach.pop();
                for(auto j:indexofval[arr[x]])
                {
                    if(!reachedindex[j])
                    {
                        toreach.push(j);
                        reachedindex[j]=true;
                    }
                }
                if((x-1)>0 && !reachedindex[x-1])
                {
                    toreach.push(x-1);
                    reachedindex[x-1]=true;
                }
                if((x+1)<arr.size() && !reachedindex[x+1])
                {
                    toreach.push(x+1);
                    reachedindex[x+1]=true;
                }
                indexofval[arr[x]].clear();//wihtout this line code gives TLE
                /*since we already reached those nodes with a particular value in less steps, then why do we need to check again to reach for those nodes with some steps 
                more than optimised steps which in worst case we need to check O(N^2) times at test case 26 I suppose....thus we clear the vector so that it never checks again*/
            }
            steps++;
        }
        return arr.size()-1;
    }
};
