        for(int i = 0; i < n; i++)
        {
            if(!numToIndex.ContainsKey(arr[i])) 
                numToIndex[arr[i]] = new List<int>();
            numToIndex[arr[i]].Add(i);
        }

        var visited = new bool[n];
        var queue = new Queue<int>();
        queue.Enqueue(0);
        visited[0] = true;
        int level = 0;
        while(queue.Count != 0)
        {
            int count = queue.Count;
            for(int cnt = 0; cnt < count; cnt++)
            {
                var curr = queue.Dequeue();
                if(curr == n - 1) return level;
                var list =  numToIndex[arr[curr]];
                if(curr - 1 >= 0) list.Add(curr - 1);
                if(curr + 1 < n) list.Add(curr + 1);
                foreach(var i in list)
                {
                    if(!visited[i])
                    {
                        visited[i] = true;
                        queue.Enqueue(i);
                    }
                }

                numToIndex[arr[curr]] = new List<int>();
            }

            level++;
        }

        return n - 1;
    }
}
