class Solution {
public:
    int minJumps(vector<int>& arr) {
        int n = arr.size(), ans = 0;
        unordered_map<int, vector<int>> m;
        for(int i=n-1; i>=0; i--) m[arr[i]].push_back(i);

        queue<pair<int, int>> q;
        q.push({arr[0], 0});

        vector<int> vis(n);
        vis[0] = 1;

        while(q.size()) {
            int l = q.size();
            while(l--) {
                int val = q.front().first, idx = q.front().second;
                q.pop();
                if(idx == n - 1) return ans;

                if(idx + 1 < n && !vis[idx + 1]) {
                    q.push({arr[idx + 1], idx + 1});
                    vis[idx + 1] = 1;
                }
                if(idx - 1 >= 0 && !vis[idx - 1]) {
                    q.push({arr[idx - 1], idx - 1});
                    vis[idx - 1] = 1;
                }
                for(int j : m[val]) {
                    if(idx == j || vis[j]) continue;
                    q.push({arr[j], j});
                    vis[j] = 1;
                }
                m.erase(val);
            }
            ans++;
        }
        return -1;
    }
};
