class Solution {
public:
    int bfs(int src, vector<int> &arr, int n, unordered_map<int, vector<int>> &m)
    {
        vector<bool> visited(n, false);
        queue<int> q;

        q.push(src);
        int steps = 0;

        while (!q.empty()) {
            int size = q.size();

            while (size--) {
                int i = q.front();
                q.pop();

                visited[i] = true;

                if (i == n - 1)
                    return steps;

                int val = arr[i];

                for (auto &j: m[val]) {
                    if (i == j || visited[j])
                        continue;

                    q.push(j);
                }

                m[val].clear();

                if (i - 1 >= 0 && !visited[i - 1])
                    q.push(i - 1);

                if (i + 1 < n && !visited[i + 1])
                    q.push(i + 1);
            }

            steps++;
        }

        return -1;
    }

    int minJumps(vector<int> &arr) {
        int n = arr.size();

        if (n == 1)
            return 0;

        if (arr[0] == arr[n - 1])
            return 1;

        unordered_map<int, vector<int>> m;

        for (int i = n - 1; i >= 0; i--)
            m[arr[i]].push_back(i);

        return bfs(0, arr, n, m);
    }
};
