# Code
```
class Solution {
public:
    int minJumps(vector<int>& arr) {
        int n = arr.size();
        vector<int> minSteps(n, INT_MAX);
        minSteps[0] = 0; // starting index

        unordered_map<int, vector<int>> indices;
        for (int i = 0; i < n; i++) {
            indices[arr[i]].push_back(i);
        }

        queue<int> q;
        q.push(0);

        while (!q.empty()) {
            int currIndex = q.front();
            q.pop();

            if (currIndex == n-1) {
                return minSteps[currIndex];
            }

            if (currIndex-1 >= 0 && minSteps[currIndex-1] == INT_MAX) {
                minSteps[currIndex-1] = minSteps[currIndex]+1;
                q.push(currIndex-1);
            }

            if (currIndex+1 < n && minSteps[currIndex+1] == INT_MAX) {
                minSteps[currIndex+1] = minSteps[currIndex]+1;
                q.push(currIndex+1);
            }

            for (int index : indices[arr[currIndex]]) {
                if (index != currIndex && minSteps[index] == INT_MAX) {
                    minSteps[index] = minSteps[currIndex]+1;
                    q.push(index);
                }
            }

            // clear the used indices to avoid revisiting them unnecessarily
            indices[arr[currIndex]].clear();
        }

        return -1; // unreachable
    }
};

