class Solution {
    public int minJumps(int[] arr) {
        int n = arr.length;
        if (n <= 1) {
            return 0;
        }

        HashMap<Integer, ArrayList<Integer>> graph = new HashMap();

        for(int i = 0; i< n; i++){
            graph.putIfAbsent(arr[i], new ArrayList());
            graph.get(arr[i]).add(i);
        }


        HashSet<Integer> curr = new HashSet();
        curr.add(0);

        HashSet<Integer> other = new HashSet();
        other.add(n - 1);

        boolean[] visited = new boolean[n];
        visited[0] = true;
        visited[n - 1] = true;

        int steps = 0;
        while(!curr.isEmpty()){

            //consider small size set
            if(curr.size() > other.size()){
                //swap sets
                HashSet<Integer> temp = curr;
                curr = other;
                other = temp;
            }

            HashSet<Integer> next = new HashSet();

            for(int currIndex : curr){

                //check same value
                for(int index : graph.get(arr[currIndex])){

                    //check already reached to this index from other end
                    if(other.contains(index)) return steps + 1;

                    if(!visited[index]){
                        visited[index] = true;
                        next.add(index);
                    }
                }


                graph.get(arr[currIndex]).clear();

                //check already reached to this index (neighbors) from other end
                if(other.contains(currIndex + 1) || other.contains(currIndex - 1)) return steps + 1;


                //check left index
                if(currIndex - 1 >= 0 && !visited[currIndex - 1]){
                    visited[currIndex - 1] = true;
                    next.add(currIndex - 1);
                }

                //check right index
                if(currIndex + 1 <= n - 1 && !visited[currIndex + 1]){
                    visited[currIndex + 1] = true;
                    next.add(currIndex + 1);
                }


            }

            curr = next;
            steps++;
        }

        return -1;
    }
}
