class Solution {
public:
    int minJumps(vector<int>& arr) {
        int n = arr.size();
        unordered_map<int,vector<int>> mp;

        for(int i=0; i<n; i++){
            mp[arr[i]].push_back(i);
        }

        int visited[100000] = {0};
        queue<int> q;
        q.push(0);
        visited[0] = 1;

        int steps = 0;

        while(!q.empty()){
            int cap = q.size();

            while(cap--){
                int curr = q.front();
                q.pop();

                if(curr == n-1) return steps;

                int left = curr - 1;
                int right = curr + 1;

                if(left >= 0 && !visited[left]){
                    q.push(left);
