```
class Solution {
    func minJumps(_ arr: [Int]) -> Int {
        var steps = 0
        var queue = Set<Int>()
        var visited = Set<Int>()

        var map = [Int: [Int]]()
        for i in 0..<arr.count {
            map[arr[i], default: [Int]()].append(i)
        }

        queue.insert(0)
        visited.insert(0)

        while !queue.isEmpty {
            var next = Set<Int>()
            while !queue.isEmpty {
                let node = queue.popFirst()!
                if node == arr.count - 1 {
                    return steps
                }

                visited.insert(node)
                if node - 1 >= 0, !visited.contains(node - 1) {
                    next.insert(node - 1)
                    visited.insert(node - 1)
                }
                if node + 1 < arr.count, !visited.contains(node + 1) {
                    next.insert(node + 1)
                    visited.insert(node + 1)
                }
                for element in (map[arr[node]] ?? []) {
                    if !visited.contains(element) {
                        next.insert(element)
                        visited.insert(element)
                    }
                }
                // Key step to avoid TLE
                // We don't need to get back to the indecies where we were
                map[arr[node]] = nil
            }
            queue = next
            steps += 1
        }

        return -1
    }
}
```

