from collections import deque

class Solution:
    def minJumps(self, arr: List[int]) -> int:
        n = len(arr)
        mapping = {}
        for i in range(n):
            if arr[i] not in mapping:
                mapping[arr[i]] = []
            mapping[arr[i]].append(i)

        visited = [False] * n
        q = deque()
        q.append(0)
        visited[0] = True
        steps = 0

        while q:
            qsize = len(q)
            for _ in range(qsize):
                i = q.popleft()
                if i == n - 1:
                    return steps
                if i - 1 >= 0 and not visited[i - 1]:
                    q.append(i - 1)
                    visited[i - 1] = True
                if i + 1 < n and not visited[i + 1]:
                    q.append(i + 1)
                    visited[i + 1] = True
                if arr[i] in mapping:
                    for neigh in mapping[arr[i]]:
                        if not visited[neigh]:
                            q.append(neigh)
                            visited[neigh] = True
                    del mapping[arr[i]]
            steps += 1

