```
func minJumps(arr []int) int {
    dict := make(map[int][]int)

    for id, num := range arr {
        dict[num] = append(dict[num], id)
    }

    visited := make([]bool , len(arr))
    queue := []int{0}
    visited[0] = true

    jumps := 0
    for len(queue) > 0 {
        l := len(queue)
        for i := 0; i < l; i++ {
            idx := queue[0]
            queue = queue[1:]            

            if idx == len(arr)-1 {
                return jumps
            }

            for _, next := range dict[arr[idx]] {
                if !visited[next] {
                    queue = append(queue, next)
                    visited[next] = true
                }
            }
            // Optimization we may check entries again and find it is already visited
            delete(dict, arr[idx])

            if idx != 0 && !visited[idx-1] {
                queue = append(queue, idx-1)
                visited[idx-1] = true
            }

            if idx+1 < len(arr) && !visited[idx+1] {
                queue = append(queue, idx+1)
                visited[idx+1] = true                
            }
        }
        jumps++
    }

    return -1
}
