        int n=arr.size();
        queue<int> q;
        vector<int> jumps(n,INT_MAX);
        q.push(src);
        jumps[src]=0;
        while(!q.empty()){
            int u=q.front();
            q.pop();
            vis[u]=true;
            for(int j: pos[arr[u]]){
                if(!vis[j] && j!=u){
                    vis[j]=true;
                    jumps[j]=min(jumps[j], jumps[u]+1);
                    q.push(j);
                }
                else if(j==u) continue;
                else break;

            }
            if(u-1>=0 && !vis[u-1]){
                jumps[u-1]=min(jumps[u-1], jumps[u]+1);
                q.push(u-1);
