class Solution {
public:
    int minJumps(vector<int>& arr) 
    {
        int n = arr.size();

        // map stores all the indices of each unique element
        unordered_map<int, vector<int>> mp;
        for (int i = 0; i < n; i++)
        {
            mp[arr[i]].push_back(i);
        }

        // visited make sures that i always visit unvisited indices
        vector<bool> visited(n,false); 
        queue<int> q;

        visited[0] = true; //visiting the first index 
        q.push(0);

        int count = 0;
        while (!q.empty()) 
        {
            int size = q.size();
            // traversing on size so that i can maintain the count of levels as we have to return the shortest path
            for (int i = 0;i < size;i++) 
            {
                //visiting the index at front of queue
                int front = q.front();
                q.pop();
                visited[front] = true;

                //if front comes out to be last index then simply return true
                if (front == n - 1)
                {
                    return count;// Reached to last index
                }

                vector<int>& next = mp[arr[front]];
                if(front - 1 >= 0 && !visited[front-1])
                {
                    next.push_back(front - 1); 
                }
                if(front + 1 < n && !visited[front+1])
                {
                    next.push_back(front + 1);
                }
                // next finally stores all the possible next steps from the front index

                //we travers on all the possible next steps 
                for (int j : next) 
                {
                    //if the next step we try to visit is already visited then there is no point in visiting the same visited node again
                    if (!visited[j]) 
                    {
                        //mark the index as visited
                        visited[j] = true;
                        q.push(j);
                    }
                }
                next.clear(); // avoid later lookup mp[arr[i]]
            }
            count++;
        }
        return count;
    }
};
