class Solution {
public:
    int minJumps(vector<int>& arr) {
        unordered_map<int,vector<int>> mp;
        int n = arr.size();
        // putting elements in map
        for(int i=0;i<n;i++) {
            mp[arr[i]].push_back(i);
        }
        // bfs for shortest path
        queue<int> q;
        q.push(0); // start index
        vector<bool> visited(n, false);
        visited[0] = true;
        int cnt = 0;
        while(!q.empty()) {
            int sz = q.size();
            while(sz--) {
                int curr = q.front(); q.pop();
                if(curr == n-1)
                    return cnt;
                if(curr-1 >= 0 && !visited[curr-1]) {
                    visited[curr-1] = true;
                    q.push(curr-1);
                }
                if(curr+1<n && !visited[curr+1]) {
                    visited[curr+1] = true;
                    q.push(curr+1);
                }
                if(mp.count(arr[curr])) {
                    // cout << curr << endl;
                    for(int indx : mp[arr[curr]]) {
                        if(indx != curr && !visited[indx]) {
                            visited[indx] = true;
                            q.push(indx);
                        }
                    }
                }
                mp.erase(arr[curr]); // to stop recomputing the on the same index 
            }
            cnt++;
        }
        return -1; // return -1 if can't reach end of the array
    }
};

