class Solution {
public:
    int minJumps(vector<int>& arr) {
        int n = arr.size();
        int tar = n - 1;

        // 3 connections, i -> i - 1, i + 1, and 
        unordered_map<int, vector<int>> sameValList; // {values : nodes}
        for (int i = 0; i < n; i++) {
            sameValList[arr[i]].push_back(i); // indicies that have the same value
        }

        queue<int> bfsQ;
        vector<bool> memo(n, false);
        bfsQ.push(0);
        memo[0] = true;

        int res = 0;
        while (!bfsQ.empty()) {
            int currQSize = bfsQ.size();
            while (currQSize-- > 0) {
                int curr = bfsQ.front(); bfsQ.pop();
                if (curr == tar) return res;
                if (curr > 0 && !memo[curr - 1]) {
                    memo[curr - 1] = true;
                    bfsQ.push(curr - 1);
                }
                if (curr + 1 < n && !memo[curr + 1]) {
                    memo[curr + 1] = true;
                    bfsQ.push(curr + 1);
                }
                // can be O(n)
                for (int &eachConnect : sameValList[arr[curr]]) {
                    if (memo[eachConnect]) continue;
                    memo[eachConnect] = true;
                    bfsQ.push(eachConnect);
                }
                sameValList[arr[curr]].clear(); // important optimization, so we dont have to recheck these connections
            }
            res++;
        }
        return -1;
    }
};
```

