class Solution {

    /**
     * @param Integer $hour
     * @param Integer $minutes
     * @return Float
     */
    function angleClock($hour, $minutes) {
        if($hour > 12) $hour -= 12; 
        $delta = abs(($minutes * 6) - (0.5 * ($hour * 60 + $minutes)));
        if($delta < 180) 
            return $delta;
        return 360-$delta;
