    final double MIN_P_H     = 5.0;
    final double CIRCLE      = 360.0;
    final double SEMI_CIRCLE = 180.0;
    final double HOURS       = 12.0;
    final double MINUTES     = 60.0;
    final double DEGREE_P_H  = 30.0;

    public double angleClock(int hour, int minutes) {
        if(minutes == hour/MIN_P_H) return 0.0;

        double result = 0.0;

        double minutes_d = degreeOfMin(minutes);
        double hour_d    = degreeOfHour(hour, minutes);
        result = Math.abs(minutes_d - hour_d);

        if(result > SEMI_CIRCLE) result = Math.abs(result - CIRCLE);
        return result;
    }

    private double degreeOfMin(int m){
        if(m == 0) return 0.0;

        double minutes_ratio = m / MINUTES;
        return minutes_ratio * CIRCLE;
    }

    private double degreeOfHour(int h, int m){
        if(h == HOURS && m == 0) return 0.0;

        double hours_ratio = h / HOURS;
        double minutes_ratio = m / MINUTES;
        double hours_degree = (hours_ratio * CIRCLE) + (minutes_ratio * DEGREE_P_H);

        if(hours_degree > CIRCLE) hours_degree -= CIRCLE;
        return hours_degree;
    }
}
```

