                if(idx==len-1)
                    return stepsTaken;
                // get all the indices with same jump length
                List<Integer> next = map.get(arr[idx]);
                // we can jump to i-1 or i+1
                // we don't want to go out of bound...
                if(idx-1>=0){
                    next.add(idx-1);
                }
                // we don't want to go out of bound...
                if(idx+1<len){
                    next.add(idx+1);
                }
                // iterate for all indices
                for(int i : next){
                    // we don't want to check the already visited indices again!
                    if(!visited[i]){
                        visited[i] = true;
                        q.offer(i);
                    }
                }
                // avoid later lookup sameValueIndicesMap arr[i]
                map.get(arr[idx]).clear();
            }
        }
        return 0;
    }
}
