A BFS Solution in C++ with comments
```
class Solution {
public:
    int minJumps(vector<int>& arr) {

        // Map to store All the indices where value is present
        map < int, vector<int> > allIndices;;

        for(int i=0; i<arr.size(); i++)
        {
            allIndices[arr[i]].push_back(i);
        }

        queue<int> Q;
        // Vector to check we only travel indice strictly once
        vector<bool> alreadyTravelled(arr.size(), false);

        Q.push(0);
        alreadyTravelled[0] = true;
        int step = 0, Qcount, currentIndex, n = arr.size();

        // To store the value of map locally 
        vector<int> a;

        // Our BFS solution Starts Here
        // if we reach indice "n-1" we have completed our BFS
        while(!alreadyTravelled[n-1])
        {
            Qcount = Q.size();

            // Iterate all Q element one by one at each step
            for(int i=0;i<Qcount;i++)
            {
                currentIndex = Q.front();
                Q.pop();

                // checking the left indice if alreadt travelled or not
                // and inserting into Q
                if(currentIndex-1 >0 && !alreadyTravelled[currentIndex-1])
                {
                    Q.push(currentIndex-1);
                    alreadyTravelled[currentIndex-1] = true;
                }

                // checking the right indice if alreadt travelled or not
                // and inserting into Q
                if(currentIndex+1 <n && !alreadyTravelled[currentIndex+1])
                {
                    Q.push(currentIndex+1);
                    alreadyTravelled[currentIndex+1] = true;
                }

                // fetching vector of current Index Value from map
                a = allIndices[arr[currentIndex]];

                // if value is present we will have non empty vector
                if(!a.empty())
                {   
                    // iterate each vector item
                    for(int k=0;k< a.size(); k++)
                    {
                        // pushing all other indices except current into Q
                        if(a[k] != currentIndex)
                        {
                            Q.push(a[k]);
                            alreadyTravelled[a[k]] = true;
                        }
                    }

                    // erasing the map for currentIndex value 
                    // this will help in not causing infinite loops
                    // Eg: {0,1,2} - indices for some value at index 0
                    // we will insert {1, 2} in the Q
                    // not erasing the map will insert "0" again when checking map for "1"
                    allIndices.erase(arr[currentIndex]);
                }
                a.clear(); // Clearing the vector for further use   
            }
            step++; // A BFS step counter
        }

        return step;
    }
};
```

