class Solution {
public:
    int minJumps(vector<int>& arr) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        /*
        Logic is to apply bfs traversal and the level at which last index is found is the shortest distance.
        For a parent (ith index) its children will be:
        -> i-1, if i-1>=0
        -> i+1, if i+1<n
        -> all its duplicate element indexes except i
        */
        int n=arr.size(), level=0;
        vector<bool> visited(n, false);
        // To store nodes during bfs
        queue<int> bfs;
        //To store duplicate indexes
        unordered_map<int, stack<int>> duplicate_index;
        for (int i=0; i<n; i++)
            duplicate_index[arr[i]].push(i);
        bfs.push(0);
        visited[0] = true;
        while(!bfs.empty()){
            int size=bfs.size();
            // after while loop ends level is increased
            while(size--){
                int i=bfs.front();
                bfs.pop();
                if (i==n-1)
                    return level;
                //i-1 jump
                if (i-1>=0 && !visited[i-1]){
                    bfs.push(i-1);
                    visited[i-1]=true;
                }
                //i+1 jump
                if (i+1<n && !visited[i+1]){
                    bfs.push(i+1);
                    visited[i+1]=true;
                }
                // jump to duplicate element
                while (!duplicate_index[arr[i]].empty()){
                    int dup_i = duplicate_index[arr[i]].top();
                    duplicate_index[arr[i]].pop();
                    if (!visited[dup_i]){
                        bfs.push(dup_i);
                        visited[dup_i]=true;
                    }
                } 
            }
            level++;
        }
        return 0;
    }
};
