class Solution {
public:

    int minJumps(vector<int>& arr) {
     if(arr.size()<=1) return 0;
     vector<int>distance(arr.size(),INT_MAX);
     distance[0]=0;
     unordered_map<int,vector<int>>mp;
     for(int i=0;i<arr.size();i++)
         mp[arr[i]].push_back(i);
     queue<int>q;
     q.push(0);
     while(!q.empty())
     {
         int f=q.front();
         q.pop();

         for(int dest:mp[arr[f]])
         {
             if(distance[dest]==INT_MAX) q.push(dest); //Visit neighbour if not visited
             distance[dest]=min(distance[dest],distance[f]+1); //Update smallest distance
         }
         if(f-1>=0)
         {
            if(distance[f-1]==INT_MAX) q.push(f-1);//Not visited
            distance[f-1]=min(distance[f-1],distance[f]+1);
         }
         if(f+1<arr.size())
         {
            if(distance[f+1]==INT_MAX) q.push(f+1);
            distance[f+1]=min(distance[f+1],distance[f]+1);
         }
         mp.erase(arr[f]);//Without this TLE //Erase all the similar elements //Because it will then search for it again 
     }
    return distance[arr.size()-1];
    }
    };

