```
class Solution {
    public int minJumps(int[] arr) {
        int n = arr.length;
        if(n<=1){
            return 0;
        }
        HashMap<Integer, List<Integer>> hm = new HashMap<>();
        for(int i=0; i<n; i++){
            hm.computeIfAbsent(arr[i], v->new LinkedList<>()).add(i);
        }
        Queue<Integer> q = new LinkedList<>();
        q.offer(0);
        int step = 0;
        Set<Integer> vis = new HashSet<>();
        while(!q.isEmpty()){
            int size = q.size();
            for(int i=0; i<size; i++){
                int item = q.poll();
                vis.add(item);
                if(item==n-1){
                    return step;
                }
                // check for same values 
                for(int child: hm.get(arr[item])){
                    if(!vis.contains(child)){
                        q.offer(child);
                    }
                }
                // check sides 
                if(item+1<n && !vis.contains(item+1)){
                    q.offer(item+1);
                }
                if(item-1>=0 && !vis.contains(item-1)){
                    q.offer(item-1);
                }
            }
            step++;
        }
        return step;
    }
}
