var minJumps = function(arr) {
    let n = arr.length
    let map = new Map()
    for (let i = 0; i < n; i++) {
        let array = []
        if (map.has(arr[i])) array = map.get(arr[i])
        array.push(i)
        map.set(arr[i], array)
    }

    let visited = new Set()
    let queue = [[0,0]]
    visited.add(0)

    while (queue.length) {
        let [cur, step] = queue.shift()
        if (cur == n-1) return step

        if (cur + 1 < n && !visited.has(cur+1)) {
            queue.push([cur+1, step+1])
            visited.add(cur+1)
        }

        if (cur - 1 >= 0 && !visited.has(cur-1)) {
            queue.push([cur-1, step+1])
            visited.add(cur-1)
        }
        if (!map.has(arr[cur])) continue
        for (let idx of map.get(arr[cur])) {
            if (!visited.has(idx) && idx != cur) {
                queue.push([idx, step+1])
                visited.add(idx)
            }
        }  

        map.delete(arr[cur])      
    }
    return -1
};
