class Solution {
public:
    int minJumps(vector<int>& arr) {
        unordered_map<int,vector<int>> mp;
        int n = arr.size();
        int i,x;
        vector<int> dp(n,-1);
        for(i=0;i<n;i++){
            mp[arr[i]].push_back(i);
        }
        dp[0]=0;
        queue<int> q;
        q.push(0);

        while(!q.empty()){
            x = q.front();
            q.pop();
            if(x+1!=n && dp[x+1]==-1){
                dp[x+1]=dp[x]+1;
                q.push(x+1);
            }
            if(x!=0 && dp[x-1]==-1){
                dp[x-1]=dp[x]+1;
                q.push(x-1);
            }
            for(auto y: mp[arr[x]]){
                if(dp[y]==-1){
                    dp[y]=dp[x]+1;
                    q.push(y);
                }
            }
            mp.erase(arr[x]); //The first time we visit 7, [7,7,7,7,7,...] all the remaining 7 will be visited by next step, hence this can be removed
        }

        return dp[n-1];
    }
};
```

