public class Solution {
    public int MinJumps(int[] arr) {
        Dictionary<int,HashSet<int>> dict = new();
        for(int i=0;i<arr.Length;i++){
            if(!dict.ContainsKey(arr[i])){
                dict.Add(arr[i],new HashSet<int>());
            }
            dict[arr[i]].Add(i);

        }

