class Solution {
public:
    bool checkIfExist(vector<int>& arr) {

        set<int> s;
        int zero_cnt = 0;
        int max_val = INT_MIN;

        //insert keys into Hash Table
        for(int i=0; i < arr.size(); i++){
            s.insert(arr[i]);
            if(arr[i]==0)
                zero_cnt++;
            max_val = max(max_val, arr[i]);
        }

        // check if there is more than one 0
        if(zero_cnt > 1)
            return true;

        // erase zero bec 2 0s case is already checked above
        s.erase(0);

        set<int>::iterator itr;
        int target;
        // target = value in the set times 2
        for(itr = s.begin(); itr!= s.end(); itr++){
            target = *itr * 2;

            // check N = 2 * M
            if(s.find(target) != s.end())
                return true;

            // if target is more than max_val in the set, no need to continue
            if(target>max_val)
                break;
        }

        return false;
     }
};       

