class Solution {
public:
    bool checkIfExist(vector<int>& a) {
        sort(a.begin(),a.end());
        int i=0;
        for(i=0;i<a.size();i++)
        {
            int temp = bs(a,2*a[i]);
            if(temp!=-1&&temp!=i)
                return true;
        }
        return false;
    }

    int bs(vector<int> a, int t)
    {
        int l=0,r=a.size()-1,mid=(l+r)/2;
        while(l<=r)
        {
            mid = (l+r)/2;
            if(a[mid]==t)
                return mid;

            if(a[mid]>t)
            r = mid -1;
            else
            l = mid + 1;
        }
        return -1;
    }
};
