// Solution 1 (Hash set)
class Solution {
public:
    bool checkIfExist(vector<int>& arr) {
        unordered_set<int> set;
        for(int i=0;i<arr.size();i++){
            if(set.count(2*arr[i])>0 || ((arr[i]%2==0) && set.count(arr[i]/2)>0))
                return true;
            set.insert(arr[i]);
        }
        return false;
    }
};

// Solution 2 (Hash table)
class Solution {
public:
    bool checkIfExist(vector<int>& arr) {
        unordered_map<int, int> mp;
        for(int i=0; i<arr.size(); i++){
            if(mp.find(arr[i]*2) != mp.end())
                return true;
            else if(arr[i]%2==0 && mp.find(arr[i]/2) != mp.end())
                return true;
            mp[arr[i]] = i;
        }
        return false;
    }
};

// Solution 3 (Two-pointers)
class Solution {
public:
    bool checkIfExist(vector<int>& arr) 
    {
        for (int i = 0; i < arr.size(); i++) {
            for (int j = i + 1; j < arr.size(); j++) {
                if ( arr[i] == (2 * arr[j]) || arr[j] == (2 * arr[i]))
                    return true;
            }
        }
        return false;
    }
};

// Solution 4 (Binary search)
class Solution {
public:
    bool binarySearch (vector<int> &arr, int l, int r, int target)
    {
        while (l <= r) {
            int m = l + (r - l) / 2;
            if (arr[m] == target)
                return true;
            else if (arr[m] > target)
                r = m - 1;
            else
                l = m + 1;
        }
        return false;
    }

    bool checkIfExist(vector<int>& arr) 
    {
        sort(arr.begin(), arr.end());
        for(int i = 0; i < arr.size(); i++) {
            if((arr[i] % 2 == 0) && arr[i] < 0) {
                if(binarySearch(arr, i + 1, arr.size() - 1, arr[i] / 2))
                    return true; 
            }
            else {
                if (binarySearch(arr, i + 1, arr.size() - 1, 2 * arr[i]))
                    return true;
            }
        }
        return false;
    }
};
