class Solution {
public:
    bool checkIfExist(vector<int>& arr) {
        unordered_map<int,int>mp;
        int n=arr.size();

        //store all the values in map
        for(int i=0;i<n;i++)
        {
            mp[arr[i]]++;
        }
        for(int i=0;i<n;i++)
        {
            //if we encounter zero, the count should be even to have double as 0*2=0
            if(arr[i]==0 && mp[arr[i]]%2==0)
                return true;
            //if we dont find the pair and also if the number is not zero since we check for zero
            if(mp.find(arr[i]*2)!=mp.end() && arr[i]!=0)
                return true;
        }
        return false;
    }
};
