            if(tFreq.containsKey(entry.getKey())){
                int diff = entry.getValue() - tFreq.get(entry.getKey());
                if(diff > 0)
                    ans += diff;
            } else {
                ans += entry.getValue();
            }
        }

        return ans;
    }
}
