class Solution {
public:
    int minSteps(string s, string t) {

        int n = s.size(),ans = 0;
        vector<int> v1(26,0),v2(26,0);

        for(int i = 0;i<n;i++)
        {
           v1[s[i]-'a']++;
           v2[t[i]-'a']++;
        }

        for(int i = 0;i<26;i++)
        ans+=abs(v1[i]-v2[i]);

        return ans/2;  
    }
};
