class Solution {
    public int minSteps(String s, String t) {
        int count = 0;
        int char_count[] = new int[26]; 

        for (int i = 0; i < s.length(); i++)  
            // Count all letters present in 1st string
            char_count[s.charAt(i) - 'a']++;

        for (int i = 0; i < t.length(); i++)  
            // Subtract all letters from the 1st string
            // If count becomes -ve, we need to modify this letter
            if (char_count[t.charAt(i) - 'a']-- <= 0) 
                count++; 

        return count;
