class Solution {
    public int minSteps(String s, String t) {
        HashMap<Character, Integer> map_s = new HashMap<>();
        HashMap<Character, Integer> map_t = new HashMap<>();

        for(char ch : s.toCharArray()) 
            map_s.put(ch, map_s.getOrDefault(ch, 0)+1);

        for(char ch : t.toCharArray()) 
            map_t.put(ch, map_t.getOrDefault(ch, 0)+1);

        int result = 0;
        for(char ch : map_t.keySet()) {
            if(map_s.get(ch) == null) result+=map_t.get(ch); // if character in string t is not in string s, we need to replace those all characters
            else if(map_s.get(ch) < map_t.get(ch)) result+=(map_t.get(ch) - map_s.get(ch)); // if # of characters in string t are more, we need to replace those extra characters.
           // else => freq is same, no replacement is required.
        }
        return result;
    }
}

