class Solution {
    func minSteps(_ s: String, _ t: String) -> Int {
        var sCountMap = [Character:Int]() 
        var tCountMap = [Character:Int]()

		// Count a number of each character appearance in s
        for c in s{
            sCountMap[c, default:0] += 1
        }
		// Count a number of each character appearance in t
        for c in t{
            tCountMap[c, default:0] += 1
        }

		// Subtract each s' character apperance from tCountMap.
        for key in sCountMap.keys{
            tCountMap[key, default:0] -= sCountMap[key]! 
        }

		// At this point, positive count in tCountMap means t has some characters appeared more than s.
		// Image we have given s="aba" t= "bab", to become anaglams, t needs to change one of "b"s to "a".
		// In our tCountMap after the subtraction, we have tCountMap as {a: -1, b: 1}.
		// The key observation here is that if t is lacking some characters by N, 
		// then there must be some other character or characters appeared more than s by N. 
		// Therefore, to count a number of character that needs to be changed in order for t to become anaglam of s, 
		// we needs to sum all the positive counts in tCountMap.
        var result = 0
        for key in tCountMap.keys{
            result += tCountMap[key]! >= 0 ? tCountMap[key]! : 0
        }

        return result
    }
}
