class Solution {
public:
    int minSteps(string s, string t) {

        unordered_map<char,int> um;
        //store frequency of all characters of s in unordered_map
        for(auto x:s)
        {
          um[x]++;  
        }
        //decrement the frequency of overlapping elements in the same unordered map 
        for(auto x:t)
        {
          um[x]--;  
        }
       int ans=0;
        //elements whose frequency is still positive must be replaced that number of times
        for(auto x:s)
        {
          if(um[x]>0)
          {
              ans+=um[x];
              um[x]=0;
          }
        }
        return ans;

    }
};
