        int result = 0;

        for(int i = 0; i < s.length(); ++i) {
            letters[s.charAt(i) - 'a']++;
            letters[t.charAt(i) - 'a']--;   
        }

        for(int i = 0; i < 26; ++i)
            if(letters[i] > 0)
                result += letters[i];

        return result;
    }
}
