class Solution:
    def minSteps(self, s: str, t: str) -> int:
        sCounter = collections.Counter(s)
        tCounter = collections.Counter(t)

        for k in tCounter.keys():
            if k in sCounter:
                tCounter[k] -= sCounter[k]
                sCounter[k] = 0
        count = 0
        for k,v in tCounter.items():
            if v > 0:
                count+=v
        return count

