class Solution {
public:
    int minSteps(string s, string t) {
        unordered_map<char,int>m;
        unordered_map<char,int>p;
        for(auto i:s){
           m[i]++;
        }
        for(auto i :t){
            p[i]++;
        }
        if(m==p){
            return 0;
        }
        int count=0;
        for(auto i:m){
            if(p[i.first]>0){
                count=count+min(m[i.first],p[i.first]);
            }
        }
        return s.length()-count;
    }
};

