                if ele != '.':
                    tags.append(f'{ele} @ {i}th row')
                    tags.append(f'{ele} @ {j}th col') 
                    tags.append(f'{ele} @ {i//3}-{j//3}th subboard')

        return len(tags) == len(set(tags))

