public:
    string toStr(int k)
    {
        stringstream ss;
        ss << k;
        string s;
        ss >> s;
        return s;
    }
    bool isValidSudoku(vector<vector<char>> &board)
    {
        map<string, int> mp;
        string temp;
        int n = board.size();
        for (int i = 0; i < n; i++)
        {
            for (int j = 0; j < n; j++)
            {
                if (board[i][j] != '.')
                {
                    //-------------------Inserting for ROW and value in its cell
                    //-------------------In case the same row contains the duplicate values, we can find it by map/ set or any DS.
                    temp = "ROW";
                    temp += toStr(i) + (board[i][j]);
                    if (mp.find(temp) == mp.end())
                    {
                        mp[temp]++;
                    }
                    else
                        return false;

                    //-------------------Inserting for COL and value in its cell
                    temp = "COL";
                    temp += toStr(j) + (board[i][j]);
                    if (mp.find(temp) == mp.end())
                    {
                        mp[temp]++;
                    }
                    else
                        return false;

                    //-------------------Inserting for BOX 
                    //-------------------There are 9 boxes of size 3x3 in sudoku
                    //-------------------and every value in box should be unique
                    temp = "BOX";
                    temp += toStr((i / 3) * 3 + (j / 3)) + (board[i][j]);
                    // cout<<temp<<endl;
                    if (mp.find(temp) == mp.end())
                    {
                        mp[temp]++;
                    }
                    else
                        return false;
                }
            }
        }
        return true;
    }
};

