class Solution {
    final int radix = 10;
    public boolean isSafe(char board[][],int row,int col,int digit){
        //column
        for(int i=0;i<=8;i++){
            if(board[i][col]==Character.forDigit(digit,radix))
               return false;
        }
        // row
        for(int j=0;j<=8;j++){
           if(board[row][j]==Character.forDigit(digit,radix))
               return false;
        }
        // grid
        int sr = (row/3)*3;
        int sc = (col/3)*3;
        for(int i=sr;i<sr+3;i++){
            for(int j=sc;j<sc+3;j++){
                if(board[i][j]==Character.forDigit(digit,radix))
                    return false;
            }
        }
        return true;
    }
    public boolean sudokoSol(char board[][],int row,int col){
        // base case
        if(row==9)
           return true;   
        // recursion work
        int nextRow = row , nextCol = col+1;
        if(nextCol==9){
            nextRow = row+1;
            nextCol=0;
        }
        if(board[row][col]!='.'){
            return sudokoSol(board,nextRow,nextCol);
        }
        for(int digit=1;digit<=9;digit++){
           if(isSafe(board,row,col,digit)){
               char c= Character.forDigit(digit,radix);
               board[row][col] = c;
               if(sudokoSol(board,nextRow,nextCol)){
                   return true;
               }
               board[row][col]='.';
           }
        }
        return false;

    }
    public boolean isValidSudoku(char[][] board) {
        if(sudokoSol(board,0,0)){
            return true;
        }
        return false;
    }
}
