func singleNumber(nums []int) int {
  m := map[int]struct{}{}
  var wholeSum int
  for _, num := range nums {
    m[num] = struct{}{}
    wholeSum += num
  }
  var uniqueSum int
  for num := range m {
    uniqueSum += num
  }
  return uniqueSum*2 - wholeSum
}
