            return;
        }

        visited.insert(i * n + j);
        vector<vector<int>> directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

        for (auto dir : directions) {
            int ni = i + dir[0];
            int nj = j + dir[1];
            dfs(A, ni, nj, visited);
        }
    }
};
